import classNames from "classnames/bind";
import styles from "./Button.module.scss";
import {Link} from "react-router-dom";

const cx = classNames.bind(styles);

function Button({ 
  to, href, large = false , small = false , primary = false , outline = false , onClick, children, ...passprops }) {
  let Comp = 'button';
  const props = {
    onClick,
    ...passprops
  };

  if(to) {
    props.to = to ;
    Comp = Link ;
  } else if(href) {
    props.href = href ;
    Comp = 'a' ;
  }

  const classes = cx('wrapper', {
    primary,
    outline,
    small,
    large
  });

  return (
    <Comp className={classes} {...props}>
      <span>{children}</span>
    </Comp>
  );
}

export default Button;
