import Tippy2 from "@tippyjs/react";
import Tippy3 from "@tippyjs/react";
import Tippy4 from "@tippyjs/react";
import "tippy.js/dist/tippy.css";

import styles from "./Header.module.scss";
import classNames from "classnames/bind";
import Button from "../../../button";
import Menu from "../../../popper/Menu";
import Image from "../../../Image";
import Search from "../Search";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faEllipsisVertical,
  faSignOut,
} from "@fortawesome/free-solid-svg-icons";
import {
  CoinIcon,
  DarkModeIcon,
  HelpIcon,
  LanguageIcon,
  LiveIcon,
  MessageIcon,
  PlusIcon,
  ProfileIcon,
  SettingIcon,
  ShotcutIcon,
  TextIcon,
} from "../../../Icons";
import { Link } from "react-router-dom";

const cx = classNames.bind(styles);
const MENU_ITEM = [
  {
    icons: <LanguageIcon />,
    title: "English",
    children: {
      title: "Language",
      data: [
        {
          code: "en",
          title: "English",
        },
        {
          code: "vi",
          title: "Tiếng Việt",
        },
      ],
    },
  },
  {
    icons: <HelpIcon />,
    title: "Feedback and help",
    to: "/feedback",
  },
  {
    icons: <ShotcutIcon />,
    title: "Keyboard shortcuts",
  },
];

function Header() {
  const currentUser = true;

  const userMenu = [
    {
      icons: <ProfileIcon />,
      title: "View profile",
      to: "/@user",
    },
    {
      icons: <CoinIcon />,
      title: "Get coin",
      to: "/coin",
    },
    {
      icons: <LiveIcon />,
      title: "LIVE Studio",
      to: "/LiveStudio",
    },
    {
      icons: <SettingIcon />,
      title: "Settings",
      to: "/settings",
    },
    ...MENU_ITEM,
    {
      icons: <DarkModeIcon />,
      title: "Dark Mode",
    },
    {
      icons: <FontAwesomeIcon icon={faSignOut} />,
      title: "Log out",
      to: "/logout",
      separate: true,
    },
  ];

  return (
    <header className={cx("wapper")}>
      <div className={cx("inner")}>
        <Link>
        </Link>

        {/* search */}
        <Search />

        <div className={cx("actions")}>
          {currentUser ? (
            <div className={cx('header-right')}>
              
              <Tippy2
                delay={[0, 200]}
                content="Upload Video"
                placement="bottom"
              >
                <button className={cx("upload-btn")}>
                  <Button simple>
                    <PlusIcon />
                    Upload
                  </Button>
                </button>
              </Tippy2>
              <Tippy3 delay={[0, 200]} content="Texts" placement="bottom">
                <div className={cx("text-btn")}>
                  <TextIcon />
                </div>
              </Tippy3>
              <Tippy4 delay={[0, 200]} content="Messages" placement="bottom">
                <div className={cx("messages-btn")}>
                  <MessageIcon />
                </div>
              </Tippy4>
            </div>
          ) : (
            <>
              <Button text>
                <PlusIcon />
                Upload
              </Button>
              <Button primary>Log in</Button>
            </>
          )}

          <Menu items={currentUser ? userMenu : MENU_ITEM}>
            {currentUser ? (
              <Image
                className={cx("user-avarta")}
                src="https://www.pngfind.com/pngs/m/685-6854994_react-logo-no-background-hd-png-download.png"
                alt="avarta"
              />
            ) : (
              <button className={cx("more-btn")}>
                <FontAwesomeIcon icon={faEllipsisVertical} />
              </button>
            )}
          </Menu>
        </div>
      </div>
    </header>
  );
}

export default Header;
